Matrix.prototype.getDeterminantEventType = function (base) {
  for (var i = 0; i < this.rows(); i += 1) {
    var isZero = true;

    for (var j = 0; j < this.cols(); j += 1) {
      var e = this.e(i, j);

      if (!e.equals(Expression.ZERO)) {
        isZero = false;
      }
    }

    if (isZero) {
      return {
        type: "special-determinant",
        row: i,
        col: -1
      };
    }
  }

  for (var j = 0; j < this.cols(); j += 1) {
    var isZero = true;

    for (var i = 0; i < this.rows(); i += 1) {
      var e = this.e(i, j);

      if (!e.equals(Expression.ZERO)) {
        isZero = false;
      }
    }

    if (isZero) {
      return {
        type: "special-determinant",
        row: -1,
        col: j
      };
    }
  }

  return {
    type: base,
    row: -1,
    col: -1
  };
}; //!new


