var polyfromtable = function (m) {
  var coefficients = new Array(m.cols());

  for (var i = 0; i < m.cols(); i += 1) {
    coefficients[i] = m.e(0, m.cols() - 1 - i);
  }

  return Polynomial.from(coefficients);
};

